                {
                    this.props.contents.map(content => {
                        return (
                            <div className="card">
                                <h2>{content.title}</h2>
                                <h5>Title description{content.updated_at}</h5>
                                <div className="fakeimg" style={{height:"200px"}}>Image</div>
                                {/* <p>{(content.body).textContent}</p> */}
                                {/* <Markup content={content.body}/> */}
